      insideTimeBox
    } = timeHandler();
    const {
      getDay,
      getDate,
      isToday,
      dateToTimestamp,
      danishDateConverter
    } = dateHandler();
    const apiData = ref();
    const loading = ref(true);
    const error = ref(false);
    const heightClass = ref('h-14');
    const timeframe = ['00:00', '02:00', '04:00', '06:00', '08:00', '10:00', '12:00', '14:00', '16:00', '18:00', '20:00', '22:00'];
    const weekHolder = ref([]);

    /**
     * Getting data from the API
     * @param date - Determine which week
     */
    const getApi = date => {
      const url = ref("http://localhost:8000/api/calendar/week");

      // Date only filled when clicking, therefor we set the loader to true
      if (date) {
        loading.value = true;
        url.value = "http://localhost:8000/api/calendar/week/" + date;
      }
      axios.get(url.value, {
        headers: {
          'Authorization': 'Bearer ' + localStorage.getItem('token')
        }
      }).then(response => response.data).then(response => {
        apiData.value = response;
        loading.value = false;
      }).catch(() => {
        error.value = true;
      });
    };
    getApi();
    const weekDays = computed(() => {
      weekHolder.value = [];
      if (apiData.value) {
        apiData.value.week.forEach(value => {
          weekHolder.value.push({
            'day': getDay(value, 'name'),
            'date': getDate(value, '/'),
            'isToday': isToday(value),
            'timestamp': value
          });
        });
      }
      return weekHolder.value;
    });
    const tasks = (timestamp, time) => {
      const tasks = apiData.value.tasks.filter(task => {
