export default function dateHandler() {
  // const dayNames = ['Mandag', 'Tirsdag', 'Onsdag', 'Torsdag', 'Fredag', 'Lørdag', 'Søndag'];
  // const dateFormat = 'd-m-yy';
  const monthNames = ['Januar', 'Februar', 'Marts', 'April', 'Maj', 'Juni', 'Juli', 'August', 'Oktober', 'September', 'November', 'December'];
  const dateLocale = 'da-DK';

  const getDay = (timestamp, format) => {
    const date = new Date(timestamp * 1000);

    if (format === 'name') {
      return date.toLocaleDateString(dateLocale, {weekday: 'long'});
    } else {
      return date.getDate();
    }
  }

  const getMonth = (timestamp, format) => {
    const date = new Date(timestamp * 1000);

    if (format === 'name') {
      return monthNames[date.getMonth()];
    } else {
      return date.getMonth()+1;
    }
  }

  const getYear = (timestamp) => {
    const date = new Date(timestamp * 1000);

    return date.getFullYear();
  }

  const getDate = (timestamp, seperator) => {
    const date = new Date(timestamp * 1000);

    return ('0' + date.getDate()).slice(-2) + '/' + ('0' + (date.getMonth()+1)).slice(-2) + '/' + date.getFullYear();

    if (seperator) {
      return date.toLocaleDateString(dateLocale).replaceAll('.', seperator);
    } else {
      return date.toLocaleDateString(dateLocale);
    }
  }

  const isToday = (timestamp) => {
    const date = new Date(timestamp * 1000);
    const today = new Date();

    return date.toLocaleDateString(dateLocale, {weekday: 'long'}) === today.toLocaleDateString(dateLocale, {weekday: 'long'});
  }

  const dateCalender = (dateParser) => {
    return new Date(dateParser).toLocaleDateString(dateLocale).replaceAll('.', '/').slice(0, 5);
  }

  return {
    getDay,
    getMonth,
    getYear,
    getDate,
    isToday,
    dateCalender
  }
}
