import "core-js/modules/es.array.push.js";
import { computed, reactive, ref } from "vue";
import dateHandler from "@/composables/dateHandler";
import timeHandler from "@/composables/timeHandler";
import Loading from "@/components/Suspense/Loading";
import axios from "axios";
export default {
  __name: 'Calendar',
  setup(__props, {
    expose
  }) {
    expose();
    const {
      insideTimeBox
    } = timeHandler();
    const {
      getDay,
      getDate,
      isToday,
      dateToTimestamp
    } = dateHandler();
    const apiData = ref();
    const loading = ref(true);
    const heightClass = ref('h-14');
    const weekHolder = ref([]);
    const timeframe = ['00:00', '02:00', '04:00', '06:00', '08:00', '10:00', '12:00', '14:00', '16:00', '18:00', '20:00', '22:00'];

    /**
     * Getting data from the API
     * @param date - Determine which week
     */
    const getApi = date => {
      const url = ref("");

      // Date only filled when clicking, therefor we set the loader to true
      if (date) {
        loading.value = true;
        // @TODO api url with parameter
      } else {
        url.value = "http://localhost:8000/api/calendar/week";
      }
      axios.get('http://localhost:8000/api/calendar/week', {
        headers: {
          'Authorization': 'Bearer ' + localStorage.getItem('token')
        }
      }).then(response => response.data).then(response => {
        apiData.value = response;
        loading.value = false;
      });
    };
    getApi();
    const weekDays = computed(() => {
      if (apiData.value) {
        apiData.value.week.forEach(value => {
          weekHolder.value.push({
            'day': getDay(value, 'name'),
            'date': getDate(value, '/'),
            'isToday': isToday(value),
            'timestamp': value
          });
        });
      }
      return weekHolder.value;
    });
    const tasks = (timestamp, time) => {
      const tasks = apiData.value.tasks.filter(task => {
        const inDayFrame = dateToTimestamp(task.task_date) === timestamp; // Checks if the task is in the day
        const inTimeFrame = insideTimeBox(String(task.task_time), String(time + ':00')); // Checks if the task is in the timeframe

        if (inDayFrame && inTimeFrame) {
          task.diff = inTimeFrame; // Assign the difference to the task object so we can use it in the component
          return task;
        }
      });

      // Return single task objects if no more than 3, otherwise return a "more task" object
      // @TODO -- Return an object if tasks more than 3
      return tasks.length < 3 ? tasks : [{
        title: '+' + tasks.length + ' Opgaver',
        moreTask: true,
        tasks: tasks.filter(task => {
          return task.id;
        })
      }];
    };
    const __returned__ = {
      insideTimeBox,
      getDay,
      getDate,
      isToday,
      dateToTimestamp,
      apiData,
      loading,
      heightClass,
      weekHolder,
      timeframe,
      getApi,
      weekDays,
      tasks,
      computed,
      reactive,
      ref,
      dateHandler,
      timeHandler,
      Loading,
      axios
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
