export default function timeHandler() {
  // Define the heights on tasks in pixel depends on task length
  const taskHeight = {
    hour: 28, // 3.5 REM
    halfHour: 14, // 1.75 REM
    minute: 0.48 // 0.03 REM
  };

  // Define the calendar view hours
  const times2Hours = [
    '00:00', '02:00', '04:00', '06:00', '08:00', '10:00', '12:00', '14:00', '16:00', '18:00', '20:00', '22:00'
  ];

  const timeCalender = (time) => {
    return time.slice(0, 5);
  }

  // Gets the difference between two hours in specified format
  const diffBetweenTwoTimes = (start, end, format) => {
    const startDate = new Date();
    const endDate = new Date();

    console.log(start);

    start = start.split(':');
    end = end.split(':');

    startDate.setHours(start[0], start[1], start[2], 0);
    endDate.setHours(end[0], end[1], end[2], 0);

    switch (format) {
      case 'minutes':
        return (endDate-startDate)/60/1000;
      case 'hours':
        return (endDate-startDate)/60/60/1000
      default:
        return endDate-startDate
    }
  }

  const timeBoxBoolean = (timeBox, taskTime) => {
    const diff = diffBetweenTwoTimes(taskTime, timeBox);

    console.log(diff);
  }

  return {
    timeCalender,
    times2Hours,
    diffBetweenTwoTimes,
    taskHeight,
    timeBoxBoolean
  }
}
