import "core-js/modules/es.array.push.js";
import { computed, reactive, ref } from "vue";
import dateHandler from "@/composables/dateHandler";
import timeHandler from "@/composables/timeHandler";
import Loading from "@/components/Suspense/Loading";
import axios from "axios";
export default {
  __name: 'Calendar',
  setup(__props, {
    expose
  }) {
    expose();
    const {
      times2Hours,
      timeCalender,
      timeBoxBoolean
    } = timeHandler();
    const {
      getDay,
      getDate,
      isToday,
      dateCalender
    } = dateHandler();
    const apiData = ref();
    const loading = ref(true);
    const heightClass = ref('h-14');
    const weekHolder = ref([]);

    /**
     *
     * @param date - Determine which week
     */
    const getTasks = date => {
      axios.get('http://localhost:8000/api/calendar/week', {
        headers: {
          'Authorization': 'Bearer ' + localStorage.getItem('token')
        }
      }).then(response => response.data).then(response => {
        apiData.value = response;
        loading.value = false;
      });
    };
    const weekDays = computed(() => {
      if (apiData.value) {
        apiData.value.week.forEach(value => {
          weekHolder.value.push({
            'day': getDay(value, 'name'),
            'date': getDate(value, '/'),
            'isToday': isToday(value)
          });
        });
      }
      return weekHolder.value;
    });
    const weekNumber = computed(() => {
      if (apiData.value) {
        return apiData.value.week_number;
      }
    });
    const tasks = (date, time) => {
      const tasks = apiData.value.tasks.filter(task => {
        const taskInBox = timeBoxBoolean(String(task.task_time), String(time + ':00'));
        if (dateCalender(task.task_date) === date.slice(0, 5) && taskInBox) {
          task.diff = taskInBox; // Assign the difference to the task object so we can use it in the component
          return task;
        }
      });

      // Return single task objects if no more than 3, otherwise return a "more task" object
      return tasks.length < 3 ? tasks : [{
        id: 5,
        title: '+' + tasks.length + ' Opgaver',
        moreTask: true
      }];
    };
    const __returned__ = {
      times2Hours,
      timeCalender,
      timeBoxBoolean,
      getDay,
      getDate,
      isToday,
      dateCalender,
      apiData,
      loading,
      heightClass,
      weekHolder,
      getTasks,
      weekDays,
      weekNumber,
      tasks,
      computed,
      reactive,
      ref,
      dateHandler,
      timeHandler,
      Loading,
      axios
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
