export default function fieldHandler() {
  const addField = (fieldsArray) => {
    fieldsArray.push({
      id: Math.random(),
      type: '',
      title: 'Overskrift',
      value: ''
    });
  }

  const deleteField = (fieldsArray, index) => {
    fieldsArray.splice(index, 1);
  }

  const setFields = (fieldsArray, fieldObj, index) => {
    return fieldsArray[index] = fieldObj;
  }

  return {
    addField, deleteField, setFields
  }
}
